mapper.select:
select * from recommend

	nick = #{nick}
	and board_idx = #{board_idx}
	and reply_idx = #{reply_idx}
	and r_reply_idx = #{r_reply_idx}

select count(*) from recommend

	and board_idx = #{board_idx}
	and reply_idx = #{reply_idx}
	and r_reply_idx = #{r_reply_idx}

mapper.delete:
delete from recommend
	
		nick=#{nick}
		and board_idx = #{board_idx}
		and reply_idx = #{reply_idx}
		and r_reply_idx = #{r_reply_idx}
